const getPeriodInMillisecond = (function getPeriodInMillisecond() {
  // time in milliseconds...
    const minute = 1000 * 60;   //     60000
    const hour = (minute * 60); //   3600000
    const day = (hour * 24);   //  86400000
    const week = (day * 7);     // 604800000

    return {
        minute(howMany) { return (howMany || 1) * minute; },
        hour(howMany) { return (howMany || 1) * hour; },
        day(howMany) { return (howMany || 1) * day; },
        week(howMany) { return (howMany || 1) * week; },
    };
}());

module.exports = getPeriodInMillisecond;

